import { ScreenHelmet, useNavigator } from '@karrotframe/navigator';
import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { decrease, increase, setDiff } from '../actions/incrementActions';
import ClickerGame from '../components/ClickerGame';
import { RootState } from '../reducers/rootReducer';

const Game = () => {
  const { push } = useNavigator();

  const handleGameEnd = () => {
    push(`/leaderboard`);
  };

  // Get state of redux store using useSelector
  const { number, diff } = useSelector((state: RootState) => ({
    number: state.increment.number,
    diff: state.increment.diff,
  }));

  // useDispatch to dispatch actions
  const dispatch = useDispatch();
  const onIncrease = () => dispatch(increase());
  // const onDecrease = () => dispatch(decrease());
  const onSetDiff = (diff: number) => dispatch(setDiff(diff));

  return (
    <div>
      <ScreenHelmet title="당근키우기" />

      <ClickerGame
        number={number}
        diff={diff}
        onIncrease={onIncrease}
        // onDecrease={onDecrease}
        onSetDiff={onSetDiff}
      />

      <button onClick={handleGameEnd}>게임끝</button>
    </div>
  );
};

export default Game;
