/** @jsxImportSource @emotion/react */
import { css } from '@emotion/react';
import { ScreenHelmet, useNavigator } from '@karrotframe/navigator';
import React from 'react';
// import Button from '../components/Button';
import { fullWidthButton } from '../components/Button';
import IndividualLeaderboard from '../components/IndividualLeaderboard';

const headingStyle = css`
  margin: 0 32px;

  font-family: Noto Sans;
  font-style: normal;
  font-weight: bold;
  font-size: 22px;
  line-height: 161.7%;
`;

const textEmphasize = css`
  color: #ff7a00;
`;

const underlinedText = css`
margin: 0;
text-decoration: none; 
position: relative; 
&:after {
  position: absolute;
  content: '';
  height: 2px;
/* adjust this to move up and down. you may have to adjust the line height of the paragraph if you move it down a lot. */
  bottom: -4px; 


 /****** 
 optional values below 
 ******/

  /* center - (optional) use with adjusting width   */
  margin: 0 auto;
left: 0;
  right: 0;
width: 50%;
background: green;

`;

const Home = () => {
  const { push } = useNavigator();
  const handleGameStart = () => {
    push(`/game`);
  };
  return (
    <div>
      <ScreenHelmet title="홈" />

      <h1 css={headingStyle}>
        <span css={textEmphasize}>로제엽떡살인마</span>님은 <br />
        우리동네에서 <span css={textEmphasize}>384793위</span> 에요!
      </h1>
      {/* <Button onClick={handleGameStart} text={`시작하기`} /> */}
      <p css={underlinedText}>주민 랭킹</p>
      <IndividualLeaderboard />
      <button css={fullWidthButton} onClick={handleGameStart}>
        시작하기
      </button>
    </div>
  );
};

export default Home;
