import { SET_DIFF, INCREASE, DECREASE } from "../actions/actionTypes";

const initialState = {
  number: 0,
  diff: 1,
}

export default function increment(state = initialState, action: { type: any; diff: any; }) {
  switch (action.type) {
    // case SET_DIFF:
    //   return {
    //     ...state,
    //     diff: action.diff
    //   };
    case INCREASE:
      return {
        ...state,
        // number: state.number + state.diff
        number: state.number + 1
      };
    // case DECREASE:
    //   return {
    //     ...state,
    //     number: state.number - state.diff
    //   };
    default:
      return state;
  }
}
